import React from 'react'
import Header from '../components/Header'
import { useSelector } from 'react-redux';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography'
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { Button } from '@material-ui/core';

const useStyles = makeStyles({
    table: {
      minWidth: 650,
    },
  });


 




const Cart = () => {
    const classes = useStyles();
    const {products}=useSelector(state=>state.CartReducer)
    console.log("prod" ,products)
    return (
        <>
        <Header/>
       
         <div style={{height:"auto",display:"flex",flexDirection:"column",justifyContent:"center",alignItems:"center"}}>
         
       
         <Typography variant="body1" color="initial">{products.length>0 ?  `You Have ${products.length} Products` :'You have empty cart' }  </Typography>
         {
             products.length>0 ? <>
               
               <TableContainer component={Paper}>
      <Table className={classes.table} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>Picture</TableCell>
            <TableCell align="right">Name</TableCell>
            <TableCell align="right">Price</TableCell>
            <TableCell align="right">Inc/Dec</TableCell>
            <TableCell align="right">Total Price</TableCell>
            <TableCell align="right">Remove</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {products.map((prod) => (
            <TableRow key={prod.name}>
              <TableCell component="th" scope="row">
                <img style={{width:"50px"}} src={prod.image} alt="img" />
              </TableCell>
              <TableCell align="right">{prod.title}</TableCell>
              <TableCell align="right">{prod.Price}</TableCell>
              <TableCell align="right">
             <div>
                  
                  <Button> Inc </Button>
                  <Button> Dec </Button>
                 </div>

              </TableCell>
              <TableCell align="right"></TableCell>
              <TableCell align="right">

                  <Button>Remove</Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>



             </>:
             "Please CHoose Your Products"
         }

             
          
      


        </div>

        </>
    )
}

export default Cart
