import React,{useEffect} from 'react'
import { useParams } from 'react-router-dom';
import { useDispatch,useSelector } from 'react-redux';
import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';






const Details = () => {
    const {id}=useParams();
    const dispatch=useDispatch()
    const {product}=useSelector(state=>state. productReducer)
    console.log(product)
    console.log(id)
    useEffect(() => {
        
        dispatch({type:"Product",id})
        
    }, [id])
    return (
        <>
        <div>
       
         <Grid container style={{flexGrow:1}}  justifyContent="space-between"
 >

         <Grid item xs={6} sm={6} md={6}>
             <div style={{width:"100%"}} >
          <img style={{width:"600px",height:"100vh",objectFit:"cover"}}  src={product.image} alt="" />
          </div>
          </Grid>

          <Grid item xs={6} sm={6} md={4}   style={{margin:"0 auto"}} >

              <Card>
                  <h3 style={{textAlign:"center"}} > {product.title} </h3>

                  </Card>

              </Grid>


     </Grid>



            
        </div>

        </>
    )
}

export default Details
