import React,{useEffect} from 'react'
import { useParams } from 'react-router-dom';
import { useDispatch,useSelector } from 'react-redux';
import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';
import { Divider, Typography } from '@material-ui/core';
import FormControl from '@material-ui/core/FormControl';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import Select from '@material-ui/core/Select';



const useStyles = makeStyles((theme) => ({
    formControl: {
      margin: theme.spacing(0.1),
      minWidth: 110,
      marginLeft:"10px",
      marginTop:"-20px"
    },
    selectEmpty: {
      marginTop: theme.spacing(2),
    },
  }));


const Details = () => {
    const classes = useStyles();
    const [state, setState] = React.useState({
        age: '',
        name: '',
      });
    
    const {id}=useParams();
    const dispatch=useDispatch()
    const {product}=useSelector(state=>state. productReducer)
    console.log(product)
    console.log(id)
    useEffect(() => {
        
        dispatch({type:"Product",id})
        
    }, [id])

    const handleChange = (event) => {
        const name = event.target.name;
        setState({
          ...state,
          [name]: event.target.value,
        });
      };
    return (
        <>
        <div>
       
         <Grid container style={{flexGrow:1}}  justifyContent="space-between"
 >

         <Grid item xs={6} sm={6} md={6}>
             <div style={{width:"100%"}} >
          <img style={{width:"600px",height:"100vh",objectFit:"cover"}}  src={product.image} alt="" />
          </div>
          </Grid>

          <Grid item xs={6} sm={6} md={4}   style={{margin:"0 auto", height:"80vh" , display:"flex",alignItems:"center" }} >

              <Card style={{padding:"50px 20px "}} >
                  <h2 style={{textAlign:"center"}} > {product.title} </h2>
                  <br/>

                  <Divider/>
                  <br/>

                  <Typography varient="body2" >{product.description}</Typography>
                  <br/>
                    
                    <div style={{display:"flex"}} >
                  <Typography style={{color:"blue"}}  variant="body1" color="initial"> Available Sizes : </Typography>
                  <div>
                  <FormControl className={classes.formControl}>
        <InputLabel htmlFor="age-native-simple">Age</InputLabel>
        <Select
          native
          value={state.age}
          onChange={handleChange}
          inputProps={{
            name: 'age',
            id: 'age-native-simple',
          }}
        >
             <option aria-label="None" value="" />
             <option value={product.availableSizes.length}></option>

            
        </Select>
      </FormControl>
                      </div>
                    
                    </div>


                  </Card>

              </Grid>


     </Grid>



            
        </div>

        </>
    )
}

export default Details
